#encode:utf-8

import os
from .wapiti import Model


here = os.path.abspath(os.path.dirname(__file__))
data_path = os.path.join(here,'data')


def load_crf_seg_model(path=None, **options):
    if path:
        options['model'] = path
    else:
        options['model'] = os.path.join(data_path,"crf_seg_model.txt")
    if os.path.exists(options['model']):
        _crf_seg_model = Model(**options)
    else:
        e = IOError()
        e.errno = 2
        e.filename = options['model']
        e.strerror = "No such file or directory"
        raise e
    return _crf_seg_model

